import React, { Component } from 'react';

import LoginView from './views/LoginView/LoginView';
import HomeView from './views/HomeView/HomeView';

import './App.css';

class App extends Component {
  constructor(props){
    super();
    this.state = {
      currUser: ""
    }
    this.props = props;
  }

  componentDidMount() {
    this.setState({
      currUser: localStorage.getItem('bookrUser')
    });
  }

  render() {
    return (
      <div className= "App">
        {ConditionalRender}
      </div>
    );
  }
}
const isLoggedIn = false;
const ConditionalRender = isLoggedIn ? <HomeView /> : <LoginView />;

export default App;
